import { Directive , HostListener , ElementRef } from '@angular/core';

@Directive({
  selector: '[appSlider]'
})
export class SliderDirective {

  constructor(private el:ElementRef) { }



      src:any = this.el.nativeElement.src;
      prev:any = document.getElementById('preview')
      flag = 0;

    @HostListener('click')
     ImageChange() {
     this.prev.src = this.src;
     let imageSlide = document.getElementsByTagName('img');
     for (let i = 0 ; i< imageSlide.length ; i++ ){
      imageSlide[i].classList.remove('active');
      console.log(imageSlide[1])

     this.el.nativeElement.classList.add('active')
   }
    let play =  setInterval(()=>{
        this.prev.src = imageSlide[this.flag].src ;
       this.flag = this.flag + 1  

     },500)

  }



}
